import React,{useState} from 'react';
import {Table } from 'antd';
import './App.css';
import Axios from 'axios';
import Tabel from './Tabel';

const App = () => {

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState(0);
  const [contactList,setContactList] = useState([]);

  const columns = [
    {
      title: 'First Name',
      dataIndex: 'firstName',
      key: 'firstName',
      render: (text) => <a>{text}</a>,
    },
    {
      title: 'Last Name',
      dataIndex: 'lastName',
      key: 'lastName',
    },
    {
      title: 'Phone Number',
      dataIndex: 'phoneNumber',
      key: 'phoneNumber',
    },
    {
      title: 'Action',
      key: 'action',
      render: () => (
          <>
          <a>Edit</a>
          <br />
          <a>Delete</a>
          </>
      ),
    },
  ];

  // const data = [

    // {
    //   key: '1',
    //   firstName: 'John Brown',
    //   lastName: 'John Brown',
    //   phoneNumber: 123,
    // },
    // {
    //   key: '2',
    //   firstName: 'Jim Green',
    //   lastName: 'Jim Green',
    //   phoneNumber: 123,
    // },
    // {
    //   key: '3',
    //   firstName: 'Joe Black',
    //   lastName: 'SOOOO',
    //   phoneNumber: 123,
    // },
  // ];

  const addContact =()=>{
    Axios.post('http://localhost:3001/create',{
      firstName:firstName,
      lastName:lastName,
      phoneNumber:phoneNumber,
    }).then(()=>{
      setContactList([
        ...contactList,
        {
          firstName:firstName,
          lastName:lastName,
          phoneNumber:phoneNumber,
        },
      ]);
    });
  }

  const getContact =()=>{
    Axios.get('http://localhost:3001/contacts').then((response)=>{
    setContactList(response.data);
    })
  };

  return(
    <>
    <div className='App'>
      <div className='information'>
        <label>First Name:</label>
        <input type="text" onChange={(e)=>{setFirstName(e.target.value)}}/>
        <label>Last Name:</label>
        <input type="text" onChange={(e)=>{setLastName(e.target.value)}}/>
        <label>Phone Number:</label>
        <input type="number" onChange={(e)=>{setPhoneNumber(e.target.value)}}/>
        <button onClick={addContact}>Add Contact</button>
      </div>
      <div>
        <button onClick={getContact}>show contacts</button>
        <Table columns={columns}  dataSource={contactList}/>;
        {contactList.map((val,key)=>{
          return (
          <div>
            <h1> {val.firstName}</h1>
            <h1> {val.lastName}</h1>
          </div>)
        })}
      </div>
      {/* <Tabel /> */}
    </div>
    </>
  )
};



export default App;
