import React,{useState} from 'react';
import {Input,Button, Table } from 'antd';
import './App.css';
import Axios from 'axios';

const App = () => {

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState(0);
  const [contactList,setContactList] = useState([]);

  const [newPhoneNumber, setNewPhoneNumber] = useState(0);


  // const columns = [
  //   {
  //     title: 'First Name',
  //     dataIndex: 'firstName',
  //     key: 'firstName',
  //     // render: (text) => <a>{text}</a>,
  //   },
  //   {
  //     title: 'Last Name',
  //     dataIndex: 'lastName',
  //     key: 'lastName',
  //   },
  //   {
  //     title: 'Phone Number',
  //     dataIndex: 'phoneNumber',
  //     key: 'phoneNumber',
  //   },
  //   {
  //     title: 'Action',
  //     key: 'action',
  //     render: () => (
  //         <>
  //         <Input style={{width:147}} type="text" placeholder='edit phone number' onChange={(e)=>{setNewPhoneNumber(e.target.value)}}/>
  //         <Button onClick={(val)=>{getNewPhoneNumber(val.id)}} style={{backgroundColor:"#69c0ff"}}>Update</Button>
  //         <br/>
  //         <Button onClick={(val)=>{deleteContact(val.id)}}>Delete</Button>
  //         </>
  //     ),
  //   },
  // ];

  const addContact =()=>{
    Axios.post('http://localhost:3001/create',{
      firstName:firstName,
      lastName:lastName,
      phoneNumber:phoneNumber,
    }).then(()=>{
      setContactList([
        ...contactList,
        {
          firstName:firstName,
          lastName:lastName,
          phoneNumber:phoneNumber,
        },
      ]);
    });
  }

  const getContact =()=>{
    Axios.get('http://localhost:3001/contacts').then((response)=>{
    setContactList(response.data);
    })
  };

  const getNewPhoneNumber =(id)=>{
    Axios.put('http://localhost:3001/update', 
    {
      phoneNumber: newPhoneNumber,
      id:id
    }).then((response)=>{
      setPhoneNumber(response.data.phoneNumber);
    }).then(alert("DataBase Updated"));
  };

  const deleteContact =(id)=>{
    Axios.get(`http://localhost:3001/delete/${id}`).then((response)=>{
    setContactList(response.data);
    })
  };

  return(
    <>
      <div className='App'>
        <div className='information'>
          <label>First Name:</label>
          <input type="text" onChange={(e)=>{setFirstName(e.target.value)}}/>
          <label>Last Name:</label>
          <input type="text" onChange={(e)=>{setLastName(e.target.value)}}/>
          <label>Phone Number:</label>
          <input type="number" onChange={(e)=>{setPhoneNumber(e.target.value)}}/>
          <button onClick={addContact}>Add Contact</button>
        </div>
        <div>
          {/* <button onClick={getContact}>show contacts</button>
          <Table columns={columns}  dataSource={contactList}/>; */}
          <button onClick={getContact}>Show Contacts</button>

          {contactList.map((val, key) => {
            return (
              <div className="employee">
                <div>
                  <h3>firstName: {val.firstName}</h3>
                  <h3>lastName: {val.lastName}</h3>
                  <h3>phoneNumber: {val.phoneNumber}</h3>
                </div>
                <div>
                  <input
                    type="text"
                    placeholder="2000..."
                    onChange={(event) => {
                      setNewPhoneNumber(event.target.value);
                    }}
                  />
                  <button
                    onClick={() => {
                      getNewPhoneNumber(val.id);
                    }}
                  >
                    {" "}
                    Update
                  </button>

                  {/* <button
                    onClick={() => {
                      deleteEmployee(val.id);
                    }}
                  >
                    Delete
                  </button> */}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </>
  )
};



export default App;
