import React,{useState} from 'react';
import {Button, Table } from 'antd';
import './App.css';
import Axios from 'axios';

const App = () => {

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [phoneNumber, setPhoneNumber] = useState(0);
  const [contactList,setContactList] = useState([]);

  const columns = [
    {
      title: 'First Name',
      dataIndex: 'firstName',
      key: 'firstName',
      // render: (text) => <a>{text}</a>,
    },
    {
      title: 'Last Name',
      dataIndex: 'lastName',
      key: 'lastName',
    },
    {
      title: 'Phone Number',
      dataIndex: 'phoneNumber',
      key: 'phoneNumber',
    },
    {
      title: 'Action',
      key: 'action',
      render: () => (
          <>
          <Input type="text" placeholder='edit phone number'/>
          <Button>Update</Button>
          </>
      ),
    },
  ];

  const addContact =()=>{
    Axios.post('http://localhost:3001/create',{
      firstName:firstName,
      lastName:lastName,
      phoneNumber:phoneNumber,
    }).then(()=>{
      setContactList([
        ...contactList,
        {
          firstName:firstName,
          lastName:lastName,
          phoneNumber:phoneNumber,
        },
      ]);
    });
  }

  const getContact =()=>{
    Axios.get('http://localhost:3001/contacts').then((response)=>{
    setContactList(response.data);
    })
  };

  return(
    <>
      <div className='App'>
        <div className='information'>
          <label>First Name:</label>
          <input type="text" onChange={(e)=>{setFirstName(e.target.value)}}/>
          <label>Last Name:</label>
          <input type="text" onChange={(e)=>{setLastName(e.target.value)}}/>
          <label>Phone Number:</label>
          <input type="number" onChange={(e)=>{setPhoneNumber(e.target.value)}}/>
          <button onClick={addContact}>Add Contact</button>
        </div>
        <div>
          <button onClick={getContact}>show contacts</button>
          <Table columns={columns}  dataSource={contactList}/>;
        </div>
      </div>
    </>
  )
};



export default App;
